mapper.sql:
FROM infrMember a WHERE 1=1 AND ifmmDelNy = #{shDelNy}
AND regDate LIKE CONCAT('%',#{shValue},'%') AND ifmmId LIKE CONCAT('%',#{shValue},'%') AND ifmmDob BETWEEN #{shDateStart} AND #{shDateEnd}
AND ifmmName LIKE CONCAT('%',#{shValue},'%') AND ifmmId LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO infrMember (
	ifmmName
	,ifmmId
	,ifmmGenderCd
	,ifmmDob
	,ifmmDelNy			
)
VALUES (
	#{ifmmName}
	,#{ifmmId}
	,#{ifmmGenderCd}
	,#{ifmmDob}
	,0
)

	SELECT last_insert_id()

mapper.update:
UPDATE
	infrMember
SET
	ifmmID = #{ifmmId}
	,ifmmName = #{ifmmName}
	,ifmmDob = #{ifmmDob}
WHERE 1=1
	AND ifmmSeq = #{ifmmSeq}
